package string;

public class Concat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s="meghana";
		System.out.println(s);
		System.out.println(s.concat(" "+"hloo"));
		s =s.concat(" "+ "hloo");
		System.out.println(s);
		System.out.println("hii" .concat("meghana"));
		s="hiii".concat("meghana");
		System.out.println(s);

	}

}
